	/**
	 *
	 * @var Customweb_Saferpay_Container
	 */
	private $container = null;

	public function __construct(Customweb_Payment_Authorization_IPaymentMethod $paymentMethod, Customweb_DependencyInjection_IContainer $container){
		parent::__construct($paymentMethod);
		if (!($container instanceof Customweb_Saferpay_Container)) {
			$container = new Customweb_Saferpay_Container($container);
		}
		$this->container = $container;
	}

	protected function getPaymentInformationMap(){
		return self::$paymentMapping;
	}

	/**
	 *
	 * @return Customweb_Saferpay_Container
	 */
	protected function getContainer(){
		return $this->container;
	}

	protected function getGlobalConfiguration(){
		return $this->getContainer()->getConfiguration();
	}


	/**
	 * Returns the Saferpay mean for this payment method.
	 *
	 * @return string ID of the payment method.
	 */
	protected function getPaymentMean(){
		$params = $this->getPaymentMethodParameters();
		return $params['mean'];
	}

	public function getPaymentMeanParameter(){
		return array(
			'PaymentMethods' => array($this->getPaymentMean()),
		
		);
	}
	
	public function getMaxOrderIdLength(){
		return 80;
	}

